import {
		CHANGE_SEARCH_FIELD,
		REQUEST_ROBOTS_PENDING,
		REQUEST_ROBOTS_SUCCEED,
		REQUEST_ROBOTS_FAILED
} from './constants';

export const setSearchField = (text) => {
	return {
		type: CHANGE_SEARCH_FIELD,
		payload: text
	}
}

export const onRobotRequest = (dispatch) => {
	dispatch({type: REQUEST_ROBOTS_PENDING});
}
